<form action="access.php" method="POST">
<label for="input"><strong>Ingresa tu número de tarjeta SUBE:   </strong></label>
  <input type="text" id="input-field" onkeyup="addSpaces()" placeholder="6061xxxxxxxxxxxx"quitarPlaceholder name="numsube" maxlength="16">
    <script>
      function addSpaces() {
        let input = document.getElementById("input-field");
        let value = input.value.replace(/\D/g, ''); // elimina caracteres no numéricos
        value = value.substring(0, 4) + " " + value.substring(4, 8) + " " + value.substring(8, 12) + " " + value.substring(12, 16);
        input.value = value.trim(); // elimina espacios en blanco al inicio y final
      }
    </script>
<script>
function quitarPlaceholder() {
  var input = document.querySelector('input[type="text"]');
  if (input.value.length > 0) {
    input.removeAttribute('placeholder');
  } else {
    input.setAttribute('placeholder', 'Ingresa tu nombre');
  }
}
</script>




