function quitarPlaceholder() {
  var input = document.querySelector('input[type="text"]');
  if (input.value.length > 0) {
    input.removeAttribute('placeholder');
  } else {
    input.setAttribute('placeholder', 'Ingresa tu nombre');
  }
}
